DECLARE @AuthType int
DECLARE @ItemName as nvarchar(425)

SET @AuthType = 1 
SET @ItemName=N'/COR_Basic' 




SELECT 
	  Catalog.ItemID 
	 ,Catalog.Path 
	 ,Catalog.Name
	,SecData.AuthType
	--,Policies.PolicyID
	 
	,SecData.XmlDescription
	,Catalog.PolicyRoot 
	,SecData.NtSecDescPrimary
	
	--,SecData.NtSecDescSecondary

	--,dbo.HexStrToVarBin('')
	--,CAST(dbo.HexStrToVarBin('') as image) 
	--,CAST(CAST(dbo.HexStrToVarBin('') as image) as varbinary(MAX))
	
	,master.sys.fn_varbintohexstr(CAST(NtSecDescPrimary AS varbinary(MAX))) AS xxx
	
	
	,Catalog.Type
FROM Catalog 

INNER JOIN Policies 
	ON Catalog.PolicyID = Policies.PolicyID 
	
LEFT JOIN SecData 
	ON Policies.PolicyID = SecData.PolicyID 
	AND AuthType = @AuthType 
	
WHERE (1=1) 
--AND Catalog.Path = @ItemName 
--AND PolicyFlag = 0 
AND 
CAST(NtSecDescPrimary as varbinary(max)) = dbo.HexStrToVarBin('')
